import React from "react";
import { Labels } from "../data/labels-const";
import { Link } from 'react-router-dom';
import './plp.scss';
import Image from './image';
import { InternaLink , StyledLink } from '../../App-styled';
import {  GetPlpProducts } from '../../components/helper/product-api';

const ContentPage = ({subTitle,loadingMessage, errorMessage}: Labels) => {
    return(
        <>
            <p> hello world</p>
            <p>{subTitle}</p>
        </>
    );
}

export default ContentPage;



const ListingImages = ({loadingMessage, errorMessage}) => {
  const {
    isLoading,
    isError,
    loadImages,
    counterRef,
    fetchPlpProducts
  } = GetPlpProducts(10);

  return(
    <>
      <div className="mylisting"> 
        <h1>Women's Clothing</h1>
        {isLoading && 
          <p className="loadingMessage">{loadingMessage}</p>
        }
        {isError &&
          <p className="errorMessage">{errorMessage}</p>
        }
        <ul className="list" >
          {loadImages.map((images) =>
            <li key={`li-${images.id}`}>
              <Image 
                key={images.id} 
                imgId = {images.id} 
                url = {images.download_url}
                imgDesc = {images.author}
              />    
            </li>
          )}
        </ul>
      </div>
      <div className="buttonsBlock">
        {/* <button onClick={() => fetchPlpProducts(true,false)}> Previous 12 images</button> 
        <button onClick={() => fetchPlpProducts(false,false)}> Next 12 images</button> */}
        <Link onClick={() => fetchPlpProducts(true, false)} to={`/plp?page=${counterRef.current - 1}`}>
          <InternaLink $firstpage={counterRef.current - 1} $primarylink>Previous</InternaLink>
        </Link>
        <p>Page {counterRef.current}</p>
        <Link onClick={() => fetchPlpProducts(false,false)} to={`/plp?page=${counterRef.current + 1}`}>
          <InternaLink $firstpage={counterRef.current + 1} $primarylink={false}>Next</InternaLink>
        </Link>

        <StyledLink currentPage={counterRef.current + 1} >Another type of next</StyledLink>
      </div>
    </>
  );
}

export default ListingImages;
