import styled from 'styled-components'

interface IconWrapperProps {
  isPrimary: boolean;
  isHeader?: boolean;
}

// Create a Wrapper component that'll render a <section> tag with some styles
export const Wrapper = styled.div`
  padding: 0 5vw 5vw;
`;

// export const StyledLink = styled(Link)`
//   color: #BF4F74;
//   font-weight: bold;
// `;

const HoverPrimaryBav  = `
  border:1px solid red;
  display:block;
  cursor:pointer;
`;

export const StyledNavLink =  styled.span<IconWrapperProps>`
  background: ${props => props.$isPrimary ? "blue" : "red"};
  border:1px solid red;
  ${HoverPrimaryBav};
  &:hover{
      background-color:red;
    }
`;
