export const Link = styled.a`
  border:1px solid red;
`;

// export const StyledLink = styled(Link)`
//   color: #BF4F74;
//   font-weight: bold;
// `;

const HoverPrimaryBav  = `
  border:1px solid red;
  display:block;
  cursor:pointer;
`;

export const StyledNavLink =  styled.span`

${HoverPrimaryBav};
&:hover{
    background-color:red;
  }
`;
